function uploadForm(fileStream, relativeName, projectName) {
    var fileDir = relativeName.split('/'),
        fileName = fileDir.pop().split('.'),
        fileType = fileName.length > 1 ? fileName.pop() : '';
    // console.log('其它可用参数：', this.queryAvailableArguments().join(', '));
    return {
        'fileDir': fileDir.join('/'),
        'fileName': fileName.join('.'),
        'fileType': fileType,
        'fileContents': fileStream
    };
}

